package com.javapractise;

public class J10StringMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String website = " www.google.com ";
		int lengthOfString = website.length();
		System.out.println(lengthOfString);
		System.out.println(website.toLowerCase());
		System.out.println(website.toUpperCase());
		System.out.println(website.trim());
		String str = String.valueOf(123.2f);
		System.out.println(str); 
		
		
		
		
		
		

	}

}
