package com.javapractise;

public class J6IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 12;
//		PostIncrement 
		System.out.println(num1++);
//		 PreIncrement
		System.out.println(++num1);
//		PostDecrement
		System.out.println(num1--);
//		PreDecrement
		System.out.println(--num1);

	}

}
